import { useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// –ò—Å–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –∏–∫–æ–Ω–∫–∏
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

function AddMarkerEvent({ onAddMarker }) {
  useMapEvents({ click: (e) => onAddMarker(e.latlng.lat, e.latlng.lng) });
  return null;
}

// –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ –¥–ª—è –≤–≤–æ–¥–∞ –¥–∞–Ω–Ω—ã—Ö
function AddMarkerModal({ position, onSave, onClose }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [type, setType] = useState('ice');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({ ...position, title, description, type, id: Date.now() });
    onClose();
  };

  return (
    <div style={{
      position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
      background: 'rgba(0,0,0,0.7)', display: 'flex',
      justifyContent: 'center', alignItems: 'center', zIndex: 1000
    }}>
      <div style={{
        background: '#2c2c2c', padding: '20px', borderRadius: '8px',
        width: '350px', color: 'white'
      }}>
        <h3>–î–æ–±–∞–≤–∏—Ç—å –º–µ—Ç–∫—É</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="–ù–∞–∑–≤–∞–Ω–∏–µ"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            style={{ width: '100%', padding: '8px', margin: '5px 0', background: '#3a3a3a', color: 'white', border: '1px solid #555' }}
            required
          />
          <textarea
            placeholder="–û–ø–∏—Å–∞–Ω–∏–µ (–æ–±–ª–µ–¥–µ–Ω–µ–Ω–∏–µ, –∑–∞–ø—Ä–∞–≤–∫–∞ –∏ —Ç.–¥.)"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            style={{ width: '100%', padding: '8px', margin: '5px 0', background: '#3a3a3a', color: 'white', border: '1px solid #555' }}
            rows="3"
            required
          />
          <select
            value={type}
            onChange={(e) => setType(e.target.value)}
            style={{ width: '100%', padding: '8px', margin: '5px 0', background: '#3a3a3a', color: 'white', border: '1px solid #555' }}
          >
            <option value="ice">–û–±–ª–µ–¥–µ–Ω–µ–Ω–∏–µ</option>
            <option value="fuel">–ó–∞–ø—Ä–∞–≤–∫–∞</option>
            <option value="heating_point">–ü—É–Ω–∫—Ç –æ–±–æ–≥—Ä–µ–≤–∞</option>
            <option value="road_damage">–†–∞–∑–º—ã–≤ –¥–æ—Ä–æ–≥–∏</option>
            <option value="danger">–û–ø–∞—Å–Ω—ã–π —É—á–∞—Å—Ç–æ–∫</option>
          </select>
          <div style={{ marginTop: '10px' }}>
            <button type="submit" style={{
              padding: '8px 16px', background: '#009688', color: 'white',
              border: 'none', borderRadius: '4px', cursor: 'pointer'
            }}>–°–æ—Ö—Ä–∞–Ω–∏—Ç—å</button>
            <button type="button" onClick={onClose} style={{
              padding: '8px 16px', background: '#555', color: 'white',
              border: 'none', borderRadius: '4px', cursor: 'pointer', marginLeft: '10px'
            }}>–û—Ç–º–µ–Ω–∞</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default function App() {
  const [markers, setMarkers] = useState(() => {
    const saved = localStorage.getItem('userMarkers');
    return saved ? JSON.parse(saved) : [];
  });
  const [modalPos, setModalPos] = useState(null);

  // –°–æ—Ö—Ä–∞–Ω—è–µ–º –º–µ—Ç–∫–∏ –≤ localStorage –ø—Ä–∏ –∏–∑–º–µ–Ω–µ–Ω–∏–∏
  const saveMarkers = (newMarkers) => {
    setMarkers(newMarkers);
    localStorage.setItem('userMarkers', JSON.stringify(newMarkers));
  };

  const handleAddMarker = (lat, lng) => {
    setModalPos({ lat, lng });
  };

  const handleSaveMarker = (marker) => {
    saveMarkers([...markers, marker]);
  };

  const closeModal = () => {
    setModalPos(null);
  };

  return (
    <div style={{ height: '100vh', width: '100%', background: 'linear-gradient(135deg, #0f2027, #203a43)' }}>
      <h2 style={{
        color: 'white', padding: '10px', margin: 0,
        background: 'rgba(10,30,47,0.9)', textAlign: 'center'
      }}>
        .Navigator –Ø–∫—É—Ç–∏–∏ üßä
      </h2>
      <MapContainer
        center={[62.0281, 129.7326]}
        zoom={6}
        style={{ height: '90%', width: '100%' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
        />
        <AddMarkerEvent onAddMarker={handleAddMarker} />
        {markers.map(marker => (
          <Marker key={marker.id} position={[marker.lat, marker.lng]}>
            <Popup>
              <b>{marker.title || marker.type}</b><br />
              {marker.description}
            </Popup>
          </Marker>
        ))}
      </MapContainer>

      {modalPos && (
        <AddMarkerModal
          position={modalPos}
          onSave={handleSaveMarker}
          onClose={closeModal}
        />
      )}
    </div>
  );
}
