import React, { useState, useEffect } from 'react';
import MapComponent from './components/MapComponent';
import WeatherWidget from './components/WeatherWidget';
import AddMarkerModal from './components/AddMarkerModal';
import POIList from './components/POIList';
import './index.css';

function App() {
  const [showModal, setShowModal] = useState(false);
  const [selectedMarker, setSelectedMarker] = useState(null);
  const [userMarkers, setUserMarkers] = useState([]);

  useEffect(() => {
    const saved = JSON.parse(localStorage.getItem('userMarkers') || '[]');
    setUserMarkers(saved);
  }, []);

  const handleAddMarker = (lat, lng) => {
    setSelectedMarker({ lat, lng });
    setShowModal(true);
  };

  const saveMarker = (marker) => {
    const newMarker = { ...marker, id: Date.now().toString() };
    const updated = [...userMarkers, newMarker];
    setUserMarkers(updated);
    localStorage.setItem('userMarkers', JSON.stringify(updated));
    setShowModal(false);
  };

  return (
    <div className="app">
      <header className="app-header">
        <h1>.Navigator Якутии</h1>
        <WeatherWidget />
      </header>
      <div className="main-content">
        <MapComponent onAddMarker={handleAddMarker} userMarkers={userMarkers} />
        <POIList markers={userMarkers} />
      </div>
      {showModal && (
        <AddMarkerModal
          position={selectedMarker}
          onClose={() => setShowModal(false)}
          onSave={saveMarker}
        />
      )}
    </div>
  );
}

export default App;
