import { useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// –ò—Å–ø—Ä–∞–≤–ª—è–µ–º –∏–∫–æ–Ω–∫—É –º–∞—Ä–∫–µ—Ä–∞
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –¥–ª—è –¥–æ–±–∞–≤–ª–µ–Ω–∏—è –º–µ—Ç–∫–∏ –ø–æ –∫–ª–∏–∫—É
function AddMarkerEvent({ onAddMarker }) {
  useMapEvents({
    click: (e) => {
      const { lat, lng } = e.latlng;
      onAddMarker(lat, lng);
    }
  });
  return null;
}

// –ì–ª–∞–≤–Ω—ã–π –∫–æ–º–ø–æ–Ω–µ–Ω—Ç
export default function App() {
  const [markers, setMarkers] = useState([]);

  const handleAddMarker = (lat, lng) => {
    const newMarker = {
      id: Date.now(),
      lat,
      lng,
      title: '–ù–æ–≤–∞—è –º–µ—Ç–∫–∞',
      description: '–û–±–ª–µ–¥–µ–Ω–µ–Ω–∏–µ / –ó–∞–ø—Ä–∞–≤–∫–∞ / –∏ —Ç.–¥.'
    };
    setMarkers([...markers, newMarker]);
  };

  return (
    <div style={{ height: '100vh', width: '100%' }}>
      <h2 style={{ color: 'white', backgroundColor: '#0f2027', padding: '10px', margin: 0 }}>
        .Navigator –Ø–∫—É—Ç–∏–∏ üó∫Ô∏è
      </h2>
      <MapContainer
        center={[62.0281, 129.7326]} // –Ø–∫—É—Ç—Å–∫
        zoom={6}
        style={{ height: '90%', width: '100%' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
        />
        <AddMarkerEvent onAddMarker={handleAddMarker} />
        {markers.map(marker => (
          <Marker key={marker.id} position={[marker.lat, marker.lng]}>
            <Popup>
              <b>{marker.title}</b><br />
              {marker.description}
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}
