import { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, Popup, useMap } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// –ò–∫–æ–Ω–∫–∞ –¥–ª—è "–º–æ–µ–≥–æ –º–µ—Å—Ç–æ–ø–æ–ª–æ–∂–µ–Ω–∏—è"
const userIcon = new L.Icon({
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
  iconSize: [25, 41],
  iconAnchor: [12, 41],
  popupAnchor: [1, -34],
  shadowSize: [41, 41]
});

// –ö–Ω–æ–ø–∫–∞ "–ú–æ—ë –º–µ—Å—Ç–æ–ø–æ–ª–æ–∂–µ–Ω–∏–µ"
function LocateUserButton({ onLocate }) {
  return (
    <button
      onClick={onLocate}
      style={{
        position: 'absolute',
        top: '80px',
        right: '20px',
        zIndex: 1000,
        padding: '10px',
        background: '#2196f3',
        color: 'white',
        border: 'none',
        borderRadius: '6px',
        cursor: 'pointer',
        fontSize: '18px',
        boxShadow: '0 2px 6px rgba(0,0,0,0.3)',
        width: '44px',
        height: '44px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}
      title="–ú–æ—ë –º–µ—Å—Ç–æ–ø–æ–ª–æ–∂–µ–Ω–∏–µ"
    >
      üìç
    </button>
  );
}

// –ö–Ω–æ–ø–∫–∞ "–î–æ–±–∞–≤–∏—Ç—å –º–µ—Ç–∫—É"
function AddMarkerButton({ onClick }) {
  const map = useMap();
  return (
    <button
      onClick={() => {
        const center = map.getCenter();
        onClick(center.lat, center.lng);
      }}
      style={{
        position: 'absolute',
        top: '140px',
        right: '20px',
        zIndex: 1000,
        padding: '10px 16px',
        background: '#009688',
        color: 'white',
        border: 'none',
        borderRadius: '6px',
        cursor: 'pointer',
        fontSize: '14px',
        boxShadow: '0 2px 6px rgba(0,0,0,0.3)',
      }}
    >
      ‚ûï –î–æ–±–∞–≤–∏—Ç—å –º–µ—Ç–∫—É
    </button>
  );
}

// –ü–µ—Ä–µ–∫–ª—é—á–∞—Ç–µ–ª—å –∫–∞—Ä—Ç
function MapLayersControl({ onLayerChange }) {
  const layers = [
    { name: 'OSM (–¥–æ—Ä–æ–≥–∏)', id: 'osm' },
    { name: '–°–ø—É—Ç–Ω–∏–∫ (ESRI)', id: 'satellite' },
    { name: '–†–µ–ª—å–µ—Ñ (Stamen)', id: 'terrain' },
    { name: '–°–≤–µ—Ç–ª–∞—è (CartoDB)', id: 'carto' },
  ];

  return (
    <div style={{
      position: 'absolute',
      top: '200px',
      right: '20px',
      zIndex: 1000,
      background: 'rgba(30,30,30,0.9)',
      borderRadius: '8px',
      padding: '8px',
      color: 'white',
      boxShadow: '0 2px 10px rgba(0,0,0,0.4)'
    }}>
      <div style={{ fontSize: '12px', marginBottom: '6px', color: '#aaa' }}>–ö–∞—Ä—Ç–∞:</div>
      {layers.map(layer => (
        <div key={layer.id} style={{ marginBottom: '4px' }}>
          <label style={{ cursor: 'pointer', display: 'flex', alignItems: 'center' }}>
            <input
              type="radio"
              name="map-layer"
              value={layer.id}
              onChange={() => onLayerChange(layer.id)}
              defaultChecked={layer.id === 'osm'}
              style={{ marginRight: '6px' }}
            />
            {layer.name}
          </label>
        </div>
      ))}
    </div>
  );
}

// –ú–æ–¥–∞–ª—å–Ω–æ–µ –æ–∫–Ω–æ –¥–æ–±–∞–≤–ª–µ–Ω–∏—è –º–µ—Ç–∫–∏
function AddMarkerModal({ position, onSave, onClose }) {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [type, setType] = useState('ice');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSave({ ...position, title, description, type, id: Date.now() });
    onClose();
  };

  return (
    <div style={{
      position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
      background: 'rgba(0,0,0,0.7)', display: 'flex',
      justifyContent: 'center', alignItems: 'center', zIndex: 2000
    }}>
      <div style={{
        background: '#1e1e1e', padding: '20px', borderRadius: '12px',
        width: '380px', color: 'white', boxShadow: '0 4px 20px rgba(0,0,0,0.5)'
      }}>
        <h3 style={{ margin: '0 0 15px 0', color: '#009688' }}>–î–æ–±–∞–≤–∏—Ç—å –º–µ—Ç–∫—É</h3>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            placeholder="–ù–∞–∑–≤–∞–Ω–∏–µ (–Ω–µ–æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ)"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            style={{
              width: '100%', padding: '10px', margin: '5px 0',
              background: '#2a2a2a', color: 'white', border: '1px solid #444',
              borderRadius: '6px'
            }}
          />
          <textarea
            placeholder="–û–ø–∏—Å–∞–Ω–∏–µ (–æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ)"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            style={{
              width: '100%', padding: '10px', margin: '5px 0',
              background: '#2a2a2a', color: 'white', border: '1px solid #444',
              borderRadius: '6px'
            }}
            rows="3"
            required
          />
          <select
            value={type}
            onChange={(e) => setType(e.target.value)}
            style={{
              width: '100%', padding: '10px', margin: '5px 0',
              background: '#2a2a2a', color: 'white', border: '1px solid #444',
              borderRadius: '6px'
            }}
          >
            <option value="ice">–û–±–ª–µ–¥–µ–Ω–µ–Ω–∏–µ</option>
            <option value="fuel">–ó–∞–ø—Ä–∞–≤–∫–∞</option>
            <option value="heating_point">–ü—É–Ω–∫—Ç –æ–±–æ–≥—Ä–µ–≤–∞</option>
            <option value="road_damage">–†–∞–∑–º—ã–≤ –¥–æ—Ä–æ–≥–∏</option>
            <option value="danger">–û–ø–∞—Å–Ω—ã–π —É—á–∞—Å—Ç–æ–∫</option>
            <option value="shop">–ú–∞–≥–∞–∑–∏–Ω</option>
          </select>
          <div style={{ marginTop: '15px', textAlign: 'right' }}>
            <button
              type="button"
              onClick={onClose}
              style={{
                padding: '8px 16px', background: '#555', color: 'white',
                border: 'none', borderRadius: '6px', cursor: 'pointer', marginRight: '10px'
              }}
            >
              –û—Ç–º–µ–Ω–∞
            </button>
            <button
              type="submit"
              style={{
                padding: '8px 16px', background: '#009688', color: 'white',
                border: 'none', borderRadius: '6px', cursor: 'pointer'
              }}
            >
              –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
            </button>
          </div>
        </form>
      </div>
    </div>
  );
}

// –°–ª–æ–∏ –∫–∞—Ä—Ç
const LAYERS = {
  osm: {
    url: 'https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png',
    attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>'
  },
  satellite: {
    url: 'https://server.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer/tile/{z}/{y}/{x}',
    attribution: 'Tiles &copy; <a href="https://services.arcgisonline.com/ArcGIS/rest/services/World_Imagery/MapServer">Esri</a>'
  },
  terrain: {
    url: 'https://stamen-tiles-{s}.a.ssl.fastly.net/terrain/{z}/{x}/{y}.png',
    attribution: 'Map tiles by <a href="https://stamen.com">Stamen Design</a>, under <a href="https://creativecommons.org/licenses/by/3.0">CC BY 3.0</a>. Data by <a href="https://openstreetmap.org">OpenStreetMap</a>',
    subdomains: 'abcd'
  },
  carto: {
    url: 'https://{s}.basemaps.cartocdn.com/light_all/{z}/{x}/{y}{r}.png',
    attribution: '&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a>, &copy; <a href="https://carto.com/attributions">CARTO</a>'
  }
};

function CustomTileLayer({ layerId }) {
  const layer = LAYERS[layerId];
  if (layerId === 'terrain') {
    return (
      <TileLayer
        url={layer.url}
        attribution={layer.attribution}
        subdomains={layer.subdomains}
      />
    );
  }
  return <TileLayer url={layer.url} attribution={layer.attribution} />;
}

export default function App() {
  const [markers, setMarkers] = useState(() => {
    const saved = localStorage.getItem('userMarkers');
    return saved ? JSON.parse(saved) : [];
  });
  const [modalPos, setModalPos] = useState(null);
  const [currentLayer, setCurrentLayer] = useState('osm');
  const [userLocation, setUserLocation] = useState(null);
  const [initialCenter, setInitialCenter] = useState([62.0281, 129.7326]); // –Ø–∫—É—Ç—Å–∫ –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é

  // –ü—Ä–∏ –∑–∞–ø—É—Å–∫–µ ‚Äî –∑–∞–ø—Ä–∞—à–∏–≤–∞–µ–º –≥–µ–æ–ª–æ–∫–∞—Ü–∏—é
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setUserLocation([latitude, longitude]);
          setInitialCenter([latitude, longitude]);
        },
        (error) => {
          console.warn('–ì–µ–æ–ª–æ–∫–∞—Ü–∏—è –Ω–µ–¥–æ—Å—Ç—É–ø–Ω–∞:', error.message);
          // –û—Å—Ç–∞—ë–º—Å—è –Ω–∞ –Ø–∫—É—Ç—Å–∫–µ
        }
      );
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('userMarkers', JSON.stringify(markers));
  }, [markers]);

  const handleAddMarker = (lat, lng) => {
    setModalPos({ lat, lng });
  };

  const handleSaveMarker = (marker) => {
    setMarkers([...markers, marker]);
    setModalPos(null);
  };

  const handleDeleteMarker = (id) => {
    setMarkers(markers.filter(m => m.id !== id));
  };

  const closeModal = () => {
    setModalPos(null);
  };

  const handleLocateUser = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          setUserLocation([latitude, longitude]);
          // –û–±–Ω–æ–≤–∏–º —Ü–µ–Ω—Ç—Ä –∫–∞—Ä—Ç—ã (—Ä–µ–∞–ª–∏–∑—É–µ–º —á–µ—Ä–µ–∑ useMap –≤ –±—É–¥—É—â–µ–º, –Ω–æ –ø–æ–∫–∞ —á–µ—Ä–µ–∑ state)
          setInitialCenter([latitude, longitude]);
        },
        (error) => {
          alert('–ù–µ —É–¥–∞–ª–æ—Å—å –æ–ø—Ä–µ–¥–µ–ª–∏—Ç—å –º–µ—Å—Ç–æ–ø–æ–ª–æ–∂–µ–Ω–∏–µ: ' + error.message);
        }
      );
    } else {
      alert('–ì–µ–æ–ª–æ–∫–∞—Ü–∏—è –Ω–µ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç—Å—è');
    }
  };

  return (
    <div style={{ height: '100vh', width: '100%', background: 'linear-gradient(135deg, #0f2027, #1a2a3a)' }}>
      <div style={{
        display: 'flex', justifyContent: 'space-between',
        alignItems: 'center', color: 'white', padding: '12px 20px',
        background: 'rgba(10, 25, 40, 0.95)', boxShadow: '0 2px 10px rgba(0,0,0,0.3)'
      }}>
        <h2 style={{ margin: 0, fontSize: '20px', fontWeight: '600' }}>Trucks Yakutia</h2>
      </div>

      <MapContainer
        center={initialCenter}
        zoom={6}
        style={{ height: 'calc(100% - 60px)', width: '100%' }}
      >
        <CustomTileLayer layerId={currentLayer} />
        {userLocation && (
          <Marker position={userLocation} icon={userIcon}>
            <Popup>–í—ã –∑–¥–µ—Å—å</Popup>
          </Marker>
        )}
        <LocateUserButton onLocate={handleLocateUser} />
        <AddMarkerButton onClick={handleAddMarker} />
        <MapLayersControl onLayerChange={setCurrentLayer} />
        {markers.map(marker => (
          <Marker key={marker.id} position={[marker.lat, marker.lng]}>
            <Popup>
              <div>
                <b>{marker.title || marker.type}</b><br />
                {marker.description}<br />
                <button
                  onClick={() => handleDeleteMarker(marker.id)}
                  style={{
                    marginTop: '8px', padding: '4px 8px',
                    background: '#d32f2f', color: 'white',
                    border: 'none', borderRadius: '4px', cursor: 'pointer',
                    fontSize: '12px'
                  }}
                >
                  –£–¥–∞–ª–∏—Ç—å
                </button>
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>

      {modalPos && (
        <AddMarkerModal
          position={modalPos}
          onSave={handleSaveMarker}
          onClose={closeModal}
        />
      )}
    </div>
  );
}
